sp_widget.client_script:
api.controller=function(spModal) {
  /* widget controller */
  var c = this;

	c.openWidgetInModal = function(widgetId, widgetInput){
		spModal.open({
			widget: widgetId,
			widgetInput: widgetInput || {},
			buttons: [],
			size: 'lg'
		}).then(function(){
		})
	};

};

